import SearchBar from '../components/SearchBar.vue';
import MugenScroll from 'vue-mugen-scroll';
import nftData from '../js/nftData';
import EventBus from '../js/EventBus';
export default {
  data: function data() {
    // do not run handler when it's loading
    return {
      receivedClickCount: 0,
      posts: [],
      posts2: [],
      count: 0,
      flag: 0,
      keyword: ''
    };
  },
  created: function created() {
    var _this = this;

    EventBus.$on('use-eventbus', function (clickedCount) {
      _this.receivedClickCount = clickedCount;
      console.log("이벤트 잘 받았음" + clickedCount);
    });
    EventBus.$on('event-flag', function (flag) {
      _this.flag = flag;
      console.log(flag);

      for (var i = 0; i < nftData.length; i++) {
        _this.posts.pop(nftData[i]);
      }
    });
    EventBus.$on('event-keyword', function (keyword) {
      _this.keyword = keyword;
    });
  },
  methods: {
    fetchData: function fetchData() {
      this.loading = true;

      for (var i = 0; i < 5; i++) {
        this.posts.push(nftData[i]);
      }

      this.loading = false;
    },
    getPosts: function getPosts() {
      for (var i = 0; i < nftData.length; i++) {
        this.posts.push(nftData[i]);
      }
    },
    searchPosts: function searchPosts() {
      this.loading = true;
      console.log("키워드:" + this.keyword);
      console.log("플래그:" + this.flag);

      for (var i = 0; i < nftData.length; i++) {
        if (this.flag == 1) {
          //1은 제목
          if (nftData[i].description.includes(this.keyword)) this.posts.push(nftData[i]);
        } else if (this.flag == 2) {
          //2는 스트리머
          if (nftData[i].streamer.includes(this.keyword)) this.posts.push(nftData[i]);
        } else if (this.flag == 3) {
          //3은 등록자
          if (nftData[i].streamer.includes(this.keyword)) this.posts.push(nftData[i]);
        } else //나머지 통합검색
          if (nftData[i].includes(this.keyword)) this.posts.push(nftData[i]);
      }

      this.loading = false;
    }
  },
  components: {
    MugenScroll: MugenScroll,
    SearchBar: SearchBar
  }
};
