import EventBus from '../js/EventBus';
export default {
  data: function data() {
    return {
      clickedCount: 0,
      searchkeyword: '',
      // 검색키워드
      searchfinish: false,
      // 검색완료시 true로 바뀌고, 이때부터 표 생성
      searchoption: ['통합검색', '제목', '스트리머', '제작자'],
      // 검색옵션
      searchoptionselected: '통합검색',
      // 검색옵션값 받아오기, 기본값은 제목으로 지정
      searchcnt: 0,
      // 검색된 게시글 갯수
      contentlist: [] // 게시글 리스트

    };
  },
  movetocontent: function movetocontent(boardnum, id) {
    // 검색된 게시글 클릭시 해당 게시글로 이동
    window.location.href = 'http://127.0.0.1:8080/board/' + boardnum + '/content?id=' + id;
  },
  methods: {
    handleGlobalClickButton: function handleGlobalClickButton() {
      this.clickedCount++;
      console.log("이벤트 송신");
      EventBus.$emit('use-eventbus', this.clickedCount);
    },
    searchstart: function searchstart() {
      // 검색버튼 눌렀을때 실행
      var searchoption = this.searchoptionselected;
      var searchkeyword = this.searchkeyword;
      console.log(searchkeyword);
      console.log(searchoption);

      if (this.searchoptionselected == '제목') {
        EventBus.$emit('event-flag', 1);
        EventBus.$emit('event-keyword', this.searchkeyword);
      } else if (this.searchoptionselected == '스트리머') {
        EventBus.$emit('event-flag', 2);
        EventBus.$emit('event-keyword', this.searchkeyword);
      } else if (this.searchoptionselected == '제작자') {
        EventBus.$emit('event-flag', 3);
        EventBus.$emit('event-keyword', this.searchkeyword);
      } else if (this.searchoptionselected == '통합검색') {
        EventBus.$emit('event-flag', 0);
        EventBus.$emit('event-keyword', this.searchkeyword);
      } else {
        EventBus.$emit('event-flag', 0);
        EventBus.$emit('event-keyword', this.searchkeyword);
      }
    }
  }
};
